correct.answer:
let count = 1...3
for _ in count {
	print("There's no place like home.")
}

var names = ["John", "Sophie", "Lottie"]
for name in names {
	print("Hello, \(name)!")
}

for i in 4...6 {
	print("Star Wars: Episode \(i)")
}

var numbers = [1, 2, 3, 4, 5, 6]
for number in numbers {
	if number % 2 == 0 {
		print(number)
	}
}

let score = 100
